package smartgraduate;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int tech = 2;
		
		switch(tech) {
		case 1:
		{
			 System.out.print("java");
			 break;
		}
		case 2:{
			  System.out.print("ES6");
			  break;
		}
		default:{
			System.out.print("Not Listed..");
			
		}  
		}
		}
	

	}
